sp_angular_provider.script:
function MeetingUserProvider($http) {

    var baseUrl = "/api/now/table/sys_user?sysparm_query=nameSTARTSWITH";
    return {
        getUserList: getUserList,
    };

    function getUserList(searchString) {
        var url = baseUrl + searchString + "&sysparm_fields=sys_id%2Cname%2Cuser_name";
        return _httpService(url, "get", null);
    }

       function _httpService(url, method, payLoad) {
        return $http({
            method: method,
            url: url,
            data: payLoad
        });
    }

}
