sp_angular_provider.script:
function MeetingApiProvider($http) {

    var baseUrl = "/api/x_1330382_meeting/meeting";

    return {
        createMeeting: createMeeting,
        getMeeting: getMeeting,
        updateMeeting: updateMeeting,
        deleteMeeting: deleteMeeting,
    };

    function getMeeting(id) {
        var url = baseUrl + "/"+id;
        return _httpService(url, "get", null);
    }

    function createMeeting(meetingDetail) {
        var url = baseUrl;
        return _httpService(url, "post", meetingDetail);
    }

    function updateMeeting(id, meetingDetail) {
        var url = baseUrl + "/" + id;
        return _httpService(url, "put",meetingDetail );
    }

    function deleteMeeting(id) {
        var url = baseUrl + "/" + id;
        return _httpService(url, "delete", null);
    }

    function _httpService(url, method, payLoad) {
        return $http({
            method: method,
            url: url,
            data: payLoad
        });
    }

}
