sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
		// gs.info("email script set_attendee_for_metting_schedule_insert start");
		// gs.info("current : "+current.getValue("parent_id"));
		var attendeeGr = new GlideRecord(new TableNames().MEETING_ATTENDEE);
		attendeeGr.addQuery("meeting_id", current.getValue("parent_id"));
        attendeeGr.query();
		gs.info("atendees found : "+attendeeGr.getRowCount());
		while(attendeeGr.next()){
			// gs.info("adding cc : "+attendeeGr.user_id.email+" : " + attendeeGr.user_id.name);
			email.addAddress("cc",attendeeGr.user_id.email.toString(),attendeeGr.user_id.name.toString());
		}

})(current, template, email, email_action, event);
