sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    try {
        var id = request.pathParams.id;
        var body = request.body.data;
        var result = new MeetingApiHandler().updateRecord(id, body);
        response.setBody({
            result
        });

    } catch (e) {
        gs.info("inside error : " + e);

        if (e instanceof sn_ws_err.NotFoundError || e instanceof sn_ws_err.BadRequestError) {
            response.setError(e);
        } else {
            var serverError = new sn_ws_err.ServiceError();
            serverError.setStatus(500);
            serverError.setMessage("internal server error");
            serverError.setDetail(e);
            response.setError(serverError);
        }
    }

})(request, response);
